                                        for index in mcast_destinations_index:
                                            multicastDestinations.append(
                                                {"arg1": pimv4_obj.PimV4JoinPruneList.href,
                                                 "arg2": ((index - 1) // device_multiplier),
                                                 "arg3": ((index - 1) % device_multiplier), "arg4": 0})
